(195, 1689100684, 1686499204, 1686499204, '02', '02', '<H2>Atividade #1.3.02</H2>\n\n   <LI>A fun&ccedil;&atilde;o \n$f_{1}(x)=x^2+x-6$\n tem os ra&iacute;zes: 2 e -3.\n       Para quais pontos iniciais: \n$[-5.0,-4.0,-1.0,1.0,3.0,5.0]$\n o algoritmo ponto fixo converge\n       usando a fun&ccedil;&atilde;o iterativa \n$&#92;varphi_1,$\n respectivamente\n$&#92;varphi_2$\n?\n       Em quantas itera&ccedil;&otilde;es?\n   </LI>\n   \n@Code ../../Run.Fixed_Point2.py\n@Exec ../../Run.Fixed_Point2.py', '2', 188, 1, '1', '1', '1', '2', '2', '', '', '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/3_Fixed_Point/9_Activities/02', '1', '02', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(196, 1689094380, 1686507291, 1686507291, 'Isolate.py', 'Isolate.py', 'NMax=10\n\ndef Isolate_Root(f,a,b):\n    N=2\n    while (N<NMax):\n        #Make sure we do division of reals\n        dx=1.0*(b-a)/(1.0*N)\n\n        x=a\n        fx=f(x)\n        \n        x1=x+dx\n        for i in range(N):\n            fx1=f(x1)\n            if (fx*fx1<0.0):\n                #Found! Return with the good news\n                return x,x1\n            x=x1\n            x1+=dx\n            fx=fx1\n\n        N+=1\n\n    print \"Invalid interval: [\"+str(a)+\",\"+str(b)+\"]\"\n    return None,None\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/0_Isolamento/Isolate.py', '3', 'Isolate.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(197, 1689094380, 1686507291, 1686507291, 'Run.py', 'Run.py', 'from Isolate import *\n\ndef f1(x):\n    return x*x-1.0\n            \n\na,b=-3.0,3.0\nprint a,\",\",b,\":\",\na,b=Isolate_Root(f1,a,b)\nprint \"[\",a,\",\",b,\"]\"\n\na,b=-3.0,1.0\nprint a,\",\",b,\":\",\na,b=Isolate_Root(f1,a,b)\nprint \"[\",a,\",\",b,\"]\"\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/0_Isolamento/Run.py', '3', 'Run.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(198, 1689094380, 1686507291, 1686507291, 'Bisection.py', 'Bisection.py', 'from Isolate import Isolate_Root\n\ndef Bisection(f,a,b,eps=1.0E-6):\n    #List of x-values\n    xs=[]\n    \n    a,b=Isolate_Root(f,a,b)\n\n    if (not a and not b):\n        return xs\n    \n    fa=f(a)\n    fb=f(b)\n    \n    \n    stop=False\n    while (not stop):\n        x=0.5*(a+b)\n        fx=f(x)\n    \n        if (fa*fx<0.0):\n            b=x\n            fb=fx\n        else:\n            a=x\n            fa=fx\n            \n        #Add to sequence\n        xs.append(x)\n        \n        if (abs(fx)<eps): stop=True\n\n    return xs\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/1_Bisection/Bisection.py', '3', 'Bisection.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(199, 1689094380, 1686507291, 1686507291, 'Functions.py', 'Functions.py', 'def Print(f,xs):\n    titles=[\"N\",\"x_k\",\"|f(x_k)|\"]\n    print \"	\".join(titles)\n    for n in range( len(xs) ):\n        comps=[\n            \"%02d\" % (n+1),\n            \"%.6f\" % xs[n],\n            \"%.6e\" % abs(f(xs[n])),\n        ]\n        print \"	\".join(comps)\n\n        \ndef f1(x):\n    return x*x-1.0\n\ndef f2(x):\n    return x**4.0-2.0*x**2.0-1.0\n\n#....\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/1_Bisection/Functions.py', '3', 'Functions.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(200, 1689094380, 1686507291, 1686507291, 'Run.Bisection.py', 'Run.Bisection.py', 'from Bisection import *\nfrom Functions import *\n\nfs=[f1,f2]\nn=1\nfor f in fs:\n    xs=Bisection(f,-3.0,3.0,1.0E-6)\n    print \"f_\"+str(n)+\":\"\n    Print(f,xs)\n    n+=1\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/1_Bisection/Run.Bisection.py', '3', 'Run.Bisection.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(201, 1689094380, 1686507291, 1686507291, 'False_Position.py', 'False_Position.py', 'from Isolate import Isolate_Root\n\ndef False_Position(f,a,b,eps=1.0E-6):\n    #Sequence (list) of x-values\n    xs=[]\n    \n    a,b=Isolate_Root(f,a,b)\n\n    if (not a and not b):\n        return xs\n    \n    fa,fb=f(a),f(b)\n    \n    stop=False\n    while (not stop):\n        x=(a*fb-b*fa)/(fb-fa)\n        fx=f(x)\n    \n        if (fa*fx<0.0):\n            b,fb=x,fx\n        else:\n            a,fa=x,fx\n            \n        #Add to sequences\n        xs.append(x)\n        \n        if (abs(fx)<eps): stop=True\n\n    return xs\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/2_False_Position/False_Position.py', '3', 'False_Position.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
