(204, 1689094380, 1686507291, 1686507291, 'Fixed_Point.py', 'Fixed_Point.py', 'def Fixed_Point(f,phi,x0,eps=1.0E-6,maxiteration=100):\n    #List of x-values\n    \n    xk=x0\n    xs=[xk]\n    \n    stop=False\n    iteration=0\n    while (not stop):\n        xk1=phi(xk)\n        \n        if ( abs(f(xk1))<eps   ): stop=True\n        if ( abs(xk1-xk)<eps   ): stop=True\n        if ( iteration>maxiteration ): stop=True\n        \n        xk=xk1\n        xs.append(xk)\n        iteration+=1\n\n    return xs\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/3_Fixed_Point/Fixed_Point.py', '3', 'Fixed_Point.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
