(207, 1689094380, 1686507291, 1686507291, '01.py', '01.py', 'from Functions3 import *\n\nfrom Newton_Raphson import Newton_Raphson\n\ndef Print(f,x):\n    i=1\n    for x in xs:\n        print str(i)+\"	\"+(\"%.6f\" % x)+\"	\"+(\"%.6e\" % abs(f(x)) )\n        i+=1\n\nfs=[f1,f2,f3]\ndfs=[df1,df2,df3]\nx0s=[1.5,1.5,1.5]\n\nn=0\nfor f in fs:\n    xs=Newton_Raphson(f,dfs[n],x0s[n],1.0E-6)\n    print \"f_\"+str(n+1)+\":\"\n    Print(f,xs)\n    n+=1\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/4_Newton_Raphson/99_Activities/01.py', '3', '01.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(208, 1689094380, 1686507291, 1686507291, 'Newton_Raphson.py', 'Newton_Raphson.py', 'def Newton_Raphson(f,df,x0,eps=1.0E-6,maxiteration=100):\n    #List of x-values\n    \n    xk=x0\n    xs=[xk]\n    \n    stop=False\n    iteration=0\n    while (not stop):\n        xk1=xk-f(xk)/df(xk)\n        \n        if ( abs(f(xk))<eps   ): stop=True\n        if ( abs(xk1-xk)<eps   ): stop=True\n        if ( iteration>maxiteration ): stop=True\n        \n        xk=xk1\n        xs.append(xk)\n        iteration+=1\n\n    return xs\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/4_Newton_Raphson/Newton_Raphson.py', '3', 'Newton_Raphson.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(209, 1689094380, 1686507291, 1686507291, '01.py', '01.py', 'from Functions3 import *\n\nfrom Secant import Secant\n\ndef Print(f,x):\n    i=1\n    for x in xs:\n        print str(i)+\"	\"+(\"%.6f\" % x)+\"	\"+(\"%.6e\" % abs(f(x)) )\n        i+=1\n\nfs=[f1,f2,f3]\ndfs=[df1,df2,df3]\nx0s=[1.5,1.5,1.5]\nx1s=[1.7,1.7,1.7]\n\nn=0\nfor f in fs:\n    \n    xs=Secant(f,x0s[n],x1s[n],1.0E-6)\n    print \"f_\"+str(n+1)+\":\"\n    Print(f,xs)\n    n+=1\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/5_Secante/99_Activities/01.py', '3', '01.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(210, 1689094380, 1686507291, 1686507291, 'Functions3.py', 'Functions3.py', 'def f1(x):\n    return x**2.0+x-6.0\n\ndef df1(x):\n    return 2.0*x+1.0\n\ndef f2(x):\n    return x**3.0-9.0*x+3.0\n\ndef df2(x):\n    return 3.0*x**2.0-9.0\n\ndef f3(x):\n    return x**2.0-2.0\n\ndef df3(x):\n    return 2.0*x\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/5_Secante/99_Activities/Functions3.py', '3', 'Functions3.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(211, 1689094380, 1686507291, 1686507291, 'Secant.py', 'Secant.py', 'def Secant(f,x0,x1,eps=1.0E-6,maxiteration=100):\n    #List of x-values\n\n    xk0=x0\n    xk1=x1\n    xs=[xk0,xk1]\n    \n    stop=False\n    iteration=0\n    while (not stop):\n        dfk=(   f(xk1)-f(xk0)   )/(  xk1-xk0  )\n        \n        xk=xk1-f(xk1)/dfk\n        \n        if ( abs(f(xk))<eps   ): stop=True\n        if ( abs(xk1-xk)<eps   ): stop=True\n        if ( iteration>maxiteration ): stop=True\n        \n        xk1=xk0\n        xk0=xk\n        xs.append(xk)\n        iteration+=1\n\n    return xs\n\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/5_Secante/Secant.py', '3', 'Secant.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(212, 1689094380, 1686507291, 1686507291, '01.py', '01.py', 'from Functions4 import *\n\nfrom Isolate        import Isolate_Root\nfrom Bisection      import Bisection\nfrom False_Position import False_Position\nfrom Fixed_Point    import Fixed_Point\nfrom Newton_Raphson import Newton_Raphson\nfrom Secant         import Secant\n\n\nfs=[f1,f2,f3,f4]\ndfs=[df1,df2,df3,df4]\nphis=[phi1,phi2,phi3,phi4]\nx0s=[1.0,1.0,0.0,2.0]\nx1s=[2.0,2.0,1.0,3.0]\n\nn=0\nfor f in fs:\n    print \"f_\"+str(n+1)+\":\"\n\n    xm=0.5*(x0s[n]+x1s[n])\n    \n    xs=Bisection(f,x0s[n],x1s[n],1.0E-6)\n    print \"Bisection\"+\"	\"+str( len(xs) )+\"	\"+str( xs[ len(xs)-1 ] )\n    \n    xs=False_Position(f,x0s[n],x1s[n],1.0E-6)\n    print \"False Position\"+\"	\"+str( len(xs) )+\"	\"+str( xs[ len(xs)-1 ] )\n    \n    xs=Fixed_Point(f,phis[n], xm  ,1.0E-6)\n    print \"Fixed Point\"+\"	\"+str( len(xs) )+\"	\"+str( xs[ len(xs)-1 ] )\n    \n    xs=Newton_Raphson(f,dfs[n], xm  ,1.0E-6)\n    print \"Newton Raphson\"+\"	\"+str( len(xs) )+\"	\"+str( xs[ len(xs)-1 ] )\n\n    xs=Secant(f,x0s[n],x1s[n]  ,1.0E-6)\n    print \"Met. Secant\"+\"	\"+str( len(xs) )+\"	\"+str( xs[ len(xs)-1 ] )\n\n    \n    n+=1\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/99_Activities/01.py', '3', '01.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(213, 1689094380, 1686507291, 1686507291, 'Functions4.py', 'Functions4.py', 'from math import *\n\ndef f1(x):\n    return exp(-x**2.0)-cos(x)\n\ndef df1(x):\n    return -2.0*x*exp(-x**2.0)+sin(x)\n\ndef phi1(x):\n    return cos(x)-exp(-x**2.0)+x\n\ndef f2(x):\n    return x**3.0-x-1.0\n\ndef df2(x):\n    return 3.0*x**2.0-1.0\n\ndef phi2(x):\n    return (x+1)**(1.0/3.0)\n\ndef f3(x):\n    return 4.0*sin(x)-exp(x)\n\ndef df3(x):\n    return 4.0*cos(x)-exp(x)\n\ndef phi3(x):\n    return x-2.0*sin(x)+0.5*exp(x)\n\ndef f4(x):\n    return x*log10(x)-1.0\n    \ndef df4(x):\n    return log10(x)+1.0/log(10)\n\ndef phi4(x):\n    return x-1.3*(  x*log10(x)-1  )\n    \n\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/0_Zeros/99_Activities/Functions4.py', '3', 'Functions4.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(214, 1689094380, 1686507291, 1686507291, 'Derivatives.py', 'Derivatives.py', 'def d_f_1(f,x,eps=1.0E-6):\n    #Simplest, somewhat naive\n    return (f(x+eps)-f(x))/eps\n\ndef d_f(f,x,eps=1.0E-6):\n    #Better\n    return (f(x+eps)-f(x-eps))/(2.0*eps)\n\ndef d2_f(f,x,eps=1.0E-6):\n    #Apply df (2*2=4 function evalutaions)\n    return (d_f(f,x+eps)-d_f(f,x-eps))/(2.0*eps)\n\ndef d2_f_1(f,x,eps=1.0E-6):\n    return (f(x+eps)+f(x-eps)-2.0*f(x))/(eps)**2\n\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/1_Derivatives/9_Activities/Derivatives.py', '3', 'Derivatives.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(215, 1689094380, 1686507291, 1686507291, 'N01.py', 'N01.py', 'from math import *\n\ndef Name1():\n    return \"x**2.0*exp(-x)\"\n\ndef f1(x):\n    return x**2*exp(-x)\n\ndef df1(x):\n    return (2.0*x-x**2)*exp(-x)\n\ndef d2f1(x):\n    return (x**2-4.0*x+2.0)*exp(-x)\n\ndef Name2():\n    return \"exp(x)/x**2.0\"\n\ndef f2(x):\n    return exp(x)/x**2.0\n\ndef df2(x):\n    return (x-2.0)*exp(x)/(x**3)\n\ndef d2f2(x):\n    return (x**2+4.0*x+2.0)*exp(x)/(x**4)\n\ndef Name3():\n    return \"log(1.0+x)\"\n\ndef f3(x):\n    return log(1.0+x)\n\ndef df3(x):\n    return 1.0/(1.0+x)\n\ndef d2f3(x):\n    return -1.0/(1.0+x)**2\n\ndef Name4():\n    return \"sqrt(1.0+x)\"\n\ndef f4(x):\n    return sqrt(1.0+x)\n\ndef df4(x):\n    return 1.0/(2.0*sqrt(1.0+x))\n\ndef d2f4(x):\n    return -0.25/((1+x)**1.5)\n\ndef Name5():\n    return \"sqrt(1.0+x**2.0)\"\n\ndef f5(x):\n    return sqrt(1.0+x**2.0)\n\ndef df5(x):\n    return 1.0*x/sqrt(1.0+x**2)\n\ndef d2f5(x):\n    return (1.0)/(   (1.0+x**2)**1.5   )\n\ndef Name6():\n    return \"cos(x)\"\n\ndef f6(x):\n    return cos(x)\n\ndef df6(x):\n    return -sin(x)\n\ndef d2f6(x):\n    return -cos(x)\n\n\ndef Name7():\n    return \"x*cos(x)\"\n\ndef f7(x):\n    return x*cos(x)\n\ndef df7(x):\n    return cos(x)-x*sin(x)\n\ndef d2f7(x):\n    return -2.0*sin(x)-x*cos(x)\n\ndef Name8():\n    return \"tan(x)\"\n\ndef f8(x):\n    return tan(x)\n\ndef df8(x):\n    return 1.0+tan(x)**2\n\ndef d2f8(x):\n    return 2.0*tan(x)*(1.0+tan(x)**2)\n\n\ndef Name9():\n    return \"sinh(x)\"\n\ndef f9(x):\n    return sinh(x)\n\ndef df9(x):\n    return cosh(x)\n\ndef d2f9(x):\n    return sinh(x)\n\ndef Name10():\n    return \"x**2.0*sinh(x)\"\n\ndef f10(x):\n    return x**2.0*sinh(x)\n\ndef df10(x):\n    return 2.0*x*sinh(x)+x**2.0*cosh(x)\n\ndef d2f10(x):\n    return 2.0*sinh(x)+4.0*x*cosh(x)+x**2*sinh(x)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/1_Derivatives/9_Activities/N01.py', '3', 'N01.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(216, 1689094380, 1686507291, 1686507291, 'N02.py', 'N02.py', 'from Derivatives import *\nfrom Residual import *\n\nfrom N01 import *\n\nxs=[1.0,2.0,5.0,10.0,20.0]\n\nfs=   [f1,   f2,   f3,   f4,   f5,   f6,   f7,   f8,   f9,   f10]\ndfs=  [df1,  df2,  df3,  df4,  df5,  df6,  df7,  df8,  df9,  df10]\nnames=[Name1,Name2,Name3,Name4,Name5,Name6,Name7,Name8,Name9,Name10]\n\nfor n in range( len(fs) ):\n    name=names[n]\n    \n    print \"--------------\"*10    \n    print \"		Function\",str(n+1),\":\",name()\n    \n    texts=[\"x\",\"Ana\",\"	d_f_1\",\"r1_a\",\"r1_r\",\"	d_f\",\"r_a\",\"r_r\", ]\n    print \"		\".join(texts)\n\n    for x in xs:\n        f=fs[n]\n        df=dfs[n]\n         \n        #Analytical\n        df_a=df(x)\n\n        #Numerical, Definition\n        df_n1=d_f_1(f,x)\n        ra1=R_Abs(df_a,df_n1)\n        rr1=R_Rel(df_a,df_n1)\n        \n        #Numerical, Symmetric\n        df_n=d_f(f,x)\n        ra=R_Abs(df_a,df_n)\n        rr=R_Rel(df_a,df_n)\n\n\n        #%.6e when we expect number to be small\n        print \"%.6f	%.6f		%.6f	%.6e	%.6e		%.6f	%.6e	%.6e\" % (x,df_a,df_n1,ra1,rr1,df_n,ra,rr)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/1_Derivatives/9_Activities/N02.py', '3', 'N02.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(217, 1689094380, 1686507291, 1686507291, 'N03.py', 'N03.py', 'from Derivatives import *\nfrom Residual import *\n\nfrom N01 import *\n\nx=1.0\nepss=[1.0E-1,1.0E-2,1.0E-4,1.0E-6,1.0E-8,1.0E-10]\n\nfs=   [f1,   f2,   f3,   f4,   f5,   f6,   f7,   f8,   f9,   f10]\ndfs=  [df1,  df2,  df3,  df4,  df5,  df6,  df7,  df8,  df9,  df10]\nnames=[Name1,Name2,Name3,Name4,Name5,Name6,Name7,Name8,Name9,Name10]\n\nfor n in range( len(fs) ):\n    name=names[n]\n    \n    print \"--------------\"*10    \n    print \"		Function\",str(n+1),\":\",name(),\"x=\",str(x)\n    \n    texts=[\"eps\",\"Ana\",\"	d_f\",\"r_a\",\"r_r\", ]\n    print \"		\".join(texts)\n\n    for eps in epss:\n        f=fs[n]\n        df=dfs[n]\n         \n        #Analytical\n        df_a=df(x)\n\n        #Numerical, Symmetric\n        df_n=d_f(f,x,eps)\n        ra=R_Abs(df_a,df_n)\n        rr=R_Rel(df_a,df_n)\n\n\n        #%.6e when we expect number to be small\n        print \"%.6e	%.6f		%.6f	%.6e	%.6e\" % (eps,df_a,df_n,ra,rr)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/1_Derivatives/9_Activities/N03.py', '3', 'N03.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(218, 1689094380, 1686507291, 1686507291, 'N04.py', 'N04.py', 'from Derivatives import *\nfrom Residual import *\n\nfrom N01 import *\n\nxs=[1.0,2.0,5.0,10.0,20.0]\n\nfs=   [f1,   f2,   f3,   f4,   f5,   f6,   f7,   f8,   f9,   f10]\nd2fs=  [d2f1,  d2f2,  d2f3,  d2f4,  d2f5,  d2f6,  d2f7,  d2f8, d2f9,  d2f10]\nnames=[Name1,Name2,Name3,Name4,Name5,Name6,Name7,Name8,Name9,Name10]\n\nfor n in range( len(d2fs) ):\n    name=names[n]\n    \n    print \"--------------\"*10    \n    print \"		Function\",str(n+1),\":\",name()\n    \n    texts=[\"x\",\"Ana\",\"	d_f_1\",\"r1_a\",\"r1_r\",\"	d_f\",\"r_a\",\"r_r\", ]\n    print \"		\".join(texts)\n    \n    f=fs[n]\n    d2f=d2fs[n]\n\n    for x in xs:\n        #Analytical\n        d2f_a=d2f(x)\n\n        #Numerical, Definition: 4 function evaluations\n        d2f_n1=d2_f_1(f,x)\n        ra1=R_Abs(d2f_a,d2f_n1)\n        rr1=R_Rel(d2f_a,d2f_n1)\n        \n        #Numerical, Symmetric: Faulty\n        d2f_n=d2_f(f,x)\n        ra=R_Abs(d2f_a,d2f_n)\n        rr=R_Rel(d2f_a,d2f_n)\n\n\n        #%.6e when we expect number to be small\n        print \"%.6f	%.6f		%.6f	%.6e	%.6e		%.6f	%.6e	%.6e\" % (x,d2f_a,d2f_n1,ra1,rr1,d2f_n,ra,rr)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/1_Derivatives/9_Activities/N04.py', '3', 'N04.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
