(221, 1689094381, 1686507292, 1686507292, 'N03.py', 'N03.py', 'from Polynomial import *\nfrom N01 import *\n\n\nxs=[-5.0,-4.0,-3.0,-2.0,-1.0,0.0,1.0,12.0,3.0,4.0,5.0]\n\nPs=[P1,P2]\n\nfor i in range( len(Ps) ):\n    P=Ps[i]\n\n    for j in range( i,len(Ps) ):\n        Q=Ps[j]\n\n        R=Polynomias_Add(P,Q)\n        for x in xs:\n            print i,j,x,Polynomia_Calc(P,x)+Polynomia_Calc(Q,x),Polynomia_Calc(R,x)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/2_Polynomials/9_Activities/N03.py', '3', 'N03.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(222, 1689094381, 1686507378, 1686507378, 'N01.py', 'N01.py', 'from math import *\n\ndef Factorial(n):\n   if (n==0):   return 1\n   elif (n==1): return 1\n   else:        return n*Factorial(n-1)\n\ndef f1(x):\n   return e**x\n\ndef Text1():\n   return \"e**x\"\n\ndef a1(n):\n   return 1.0/(1.0*Factorial(n))\n\ndef f2(x):\n   return cos(x)\n\ndef Text2():\n   return \"cos(x)\"\n\ndef a2(n):\n   if ( (n%2)==1): return 0.0\n\n   p=n/2\n   return (-1.0)**p/(1.0*Factorial(n))\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/3_Taylor/9_Activities/N01.py', '3', 'N01.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(223, 1689094381, 1686507378, 1686507378, 'N02.py', 'N02.py', 'from math import *\n\nfrom Polynomial import *\nfrom Residual import *\n\nfrom N01 import *\n\n\ndef Calc_Taylor_Pol(a,n):\n   P=[]\n   for i in range(n+1):\n      P.append( a(i) )\n\n   return P\n\nfs=[f1,f2]\nass=[a1,a2]\ntexts=[Text1,Text2]\n\nx=0.5\nfor i in range( len(fs) ):\n   f=fs[i]\n   a=ass[i]\n   text=texts[i]\n\n   print text()\n\n   print \"x		n	Analytical	Approx		Absolute	Relative\"\n   \n   for n in range(10):\n      P=Calc_Taylor_Pol(a,n)\n\n      #Polynomia_Print(P)\n      \n      f_ana=f(x)\n      f_tay=Polynomia_Calc(P,x)\n\n      print \"%.6f	%d	%.6f	%.6f	%.6e	%.6e\" % (x,n,f_ana,f_tay,R_Abs(f_ana,f_tay),R_Rel(f_ana,f_tay))\n   \n\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/1_Functions/3_Taylor/9_Activities/N02.py', '3', 'N02.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
