(225, 1689094381, 1686507378, 1686507378, 'N02.py', 'N02.py', '\nfrom Vector import *\n\nfrom N01 import *\n\nfor i in range( len(v) ):\n    for j in range( len(v) ):\n        w=Vectors_Add(v[i],v[j])\n        print i,j,v[i],\"+\",v[j],\"=\",w\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/2_Linear/1_Vectors/9_Activities/N02.py', '3', 'N02.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(226, 1689094381, 1686507378, 1686507378, 'Vector.py', 'Vector.py', 'from math import sqrt\n\ndef Vector_Print(v):\n    text=[]\n    for i in range( len(v) ):\n        text.append( \"%.6f\" % v[i] )\n\n    print \"[\"+\",\".join(text)+\"]\"\n\ndef Vectors_Add(v,w):\n    if (   len(v)!=len(w)   ):\n        print \"Vectors_Add: Vectors does not have same length\";\n        exit();\n        \n    u=[]\n    for i in range( len(v) ):\n        u.append( 1.0*(v[i]+w[i]) )\n\n    return u\n\ndef Vectors_Sub(v,w):\n    if (   len(v)!=len(w)   ):\n        print \"Vectors_Add: Vectors does not have same length\";\n        exit();\n        \n    u=[]\n    for i in range( len(v) ):\n        u.append( 1.0*(v[i]-w[i]) )\n\n    return u\n\n        \ndef Vector_Mul(v,a):\n    u=[]\n    for i in range( len(v) ):\n        u.append( a*v[i] )\n\n    return u\n\n        \n\ndef Vector_CL(a1,v1,a2,v2):\n    if (   len(v1)!=len(v2)   ):\n        print \"Vectors_CL: Vectors does not have same length\";\n        exit();\n        \n    a1*=1.0\n    a2*=1.0\n    \n    u=[]\n    for i in range( len(v) ):\n        u.append( a1*v1[i]+a2*v2[i] )\n\n    return u\n\ndef Vector_Dot(v,w):\n    if (   len(v)!=len(w)   ):\n        print \"Vectors_Dot: Vectors does not have same length\";\n        exit();\n        \n    dot=0.0\n    for i in range( len(v) ):\n        dot+=v[i]*w[i]\n\n    return dot\n\ndef Vectors_Angle(v,w):\n    return Vector_Dot(v,w)/(   Vector_Len(v)*Vector_Len(w)   )\n\ndef Vector_SqLen(v):\n    return Vector_Dot(v,v)\n\ndef Vector_Len(v):\n    return sqrt(  Vector_SqLen(v)  )\n\ndef Vector_Normalize(v):\n    length=Vector_Len(v)\n\n    e=list(v)\n    if (length>0.0):\n        e=Vector_Mul( v,1.0/length )\n        \n    return e\n\ndef Vector_Norm(v,p=2.0):\n    norm=0.0\n    for i in range( len(v) ):\n        norm+= abs(v[i])**p\n        \n    return norm**(1.0/p)\n\ndef Vector_Norm_Inf(v):\n    norm=0.0\n    for i in range( len(v) ):\n        if (abs(v[i])>norm):\n            norm=abs(v[i])\n        \n    return norm\n\ndef Vector_Project(v,w):\n    #Projects Vector v on Vector w\n    ww=Vector_Normalize(w)\n    dot=Vector_Dot(v,ww)\n    \n    return Vector_Mul(ww,dot)\n\ndef Vector_Complement(v,w):\n    p=Vector_Project(v,w)\n\n    return  Vector_Sub(v,p)\n\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/2_Linear/1_Vectors/Vector.py', '3', 'Vector.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
