(229, 1689094381, 1686507379, 1686507379, 'Matrix.py', 'Matrix.py', 'from Vector import *\n\ndef Matrix_Print(A):\n    print \"[\"\n    for i in range( len(A) ):\n        print \"   \",\n        Vector_Print(A[i])\n\n    print \"]\"\n\ndef Matrix_Copy(A):\n    B=[]\n    for i in range( len(A) ):\n        B.append([])\n        for j in range( len(A[i]) ):\n            B[i].append(A[i][j])\n\n    return B\n\ndef Matrix_Zero(m,n=0):\n    if (n==0): n=m\n    \n    O=[]\n    for i in range(m):\n        O.append([])\n        for j in range(n):\n            O[i].append(0.0)\n\n    return O\n\ndef Matrix_Identity(n):\n    I=Matrix_Zero(n)\n    for i in range(n):\n        I[i][i]=1.0\n\n    return I\n\ndef Matrix_Vandermonte(v):\n    V=[]\n    for i in range( len(v) ):\n        V.append([])\n        \n        fact=1.0\n        for j in range( len(v) ):\n            V[i].append(fact)\n            fact*=v[i]\n\n    return V\n\n\n\ndef Matrices_Add(A,B):\n    if (   len(A)!=len(B)   ):\n        print \"Matrices_Add: Matrices does not have same no of rows\"\n        exit()\n\n    C=[]\n    for i in range( len(A) ):\n        C.append(  []  )\n        if (   len(A[i])!=len(B[i])   ):\n            print \"Matrices_Add: Matrices does not have same no of columns\"\n            exit()\n\n        for j in range( len(A[i]) ):\n            C[i].append(   1.0*(A[i][j]+B[i][j])   )\n            \n    return C\n\ndef Matrices_Sub(A,B):\n    if (   len(A)!=len(B)   ):\n        print \"Matrices_Add: Matrices does not have same no of lines\"\n        exit()\n\n    C=[]\n    for i in range( len(A) ):\n        C.append(  []  )\n        if (   len(A[i])!=len(B[i])   ):\n            print \"Matrices_Add: Matrices does not have same no of columns\"\n            exit()\n\n        for j in range( len(A[i]) ):\n            C[i].append(   1.0*(A[i][j]-B[i][j])   )\n            \n    return C\n\ndef Matrix_Mult_Scalar(A,a):\n    C=[]\n    for i in range( len(A) ):\n        C.append(  []  )\n        for j in range( len(A[i]) ):\n            C[i].append(   a*A[i][j]  )\n            \n    return C\n\ndef Matrix_Mult_Vector(A,v):\n    if (   len(A[0])!=len(v)   ):\n        print \"Matrices_Mult_Vector: Matrices and Vector has incompatible dimensions\"\n        exit()\n    w=[]\n    for i in range( len(A) ):\n        sum=0.0\n        for j in range( len(A[i]) ):\n            sum+=1.0*A[i][j]*v[j]\n\n        w.append(sum)\n    return w\n\ndef Matrices_Mult(A,B):\n    if (   len(A[0])!=len(B)   ):\n        print \"Matrices_Mult: Matrices has incompatible dimensions\"\n        exit()\n\n    C=[]\n    for i in range( len(A) ):\n        C.append( [] )\n        for j in range( len(B[0]) ):\n            sum=0.0\n            for k in range( len(B) ):\n                sum+=1.0*A[i][k]*B[k][j]\n\n            C[i].append(sum)\n    return C\n\ndef Matrix_Norm(A,p=2.0):\n    norm=0.0\n    for i in range( len(A) ):\n        for j in range( len(A[i]) ):\n            norm+= abs(A[i][j])**p\n        \n    return norm**(1.0/p)\n\ndef Matrices_Row_Mult(A,i,a):\n    for k in range( len(A[i]) ):\n        A[i][k]*=a\n        \n\ndef Matrices_Rows_Swap(A,i,j):\n    if (i==j): return\n    \n    for k in range( len(A[i]) ):\n        tmp=A[i][k]\n        A[i][k]=A[j][k]\n        A[j][k]=tmp\n\ndef Matrices_Rows_Operation(A,i,c,j):\n    if (i==j): return\n    \n    for k in range( len(A[i]) ):\n        A[i][k]+=c*A[j][k]\n\n\ndef Matrix_Determinant(A):\n    return Matrix_Gauss_Forward(A)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/2_Linear/2_Matrices/Matrix.py', '3', 'Matrix.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
