(261, 1689092601, 1686507380, 1686507380, 'N05.py', 'N05.py', 'from math import pi\n\ndef a(n,p):\n    return 1.0/( 1.0*n**(2*p)  )\n\ndef f(p):\n    if (p==1):   return 6.0\n    elif (p==2): return 90.0\n    elif (p==3): return 945.0\n    elif (p==4): return 9450.0\n    elif (p==5): return 93555.0\n    elif (p==6): return 924041.787265\n    else:        print \"Invalid p\"\n\n\ndef s(p):\n    return pi**(2*p)/f(p)\n\n\ndef Partial(n,p):\n    #N terms\n    summ=0.0\n    for i in range(1,n+1):\n        summ+= a(i,p)\n\n    return summ\n\ndef ff(p,n):\n    sum_num=Partial(n,p)\n\n    return pi**(2*p)/sum_num\n    \n    \n\n########## 2.a e 2.b #################\n\nps=[1,2,3,4,5,6]\nns=[10,100,1000,10000,100000]\n\neps=1.0E-10\nnmax=10000000\nfor p in ps:\n    latex=[]\n    latex.append(\"\\begin{tabular}{lllllll}\")\n    \n    text=[\n        \"$p$\",\"$n$\",\n        \"$s$\",\"$s_n$\",\n        \"$r_a$\",\"$r_r$\",\"$pi^{2p}/s_p$\",\n    ]\n\n    latex.append(   \" & \".join(text)+\"\\\\\"    )\n\n    err=1.0\n    n=1\n    while (err>eps and n<nmax):\n        n*=10\n        sum_ana=s(p)\n        sum_num=Partial(n,p)\n\n        ff_num=ff(p,n)\n\n        r_a=abs(sum_ana-sum_num)\n        r_r=r_a/abs(sum_ana)\n\n        text=[\n            \"%d\" % p,\n            \"%d\" % n,\n            \"%.6f\" % sum_ana,\n            \"%.6f\" % sum_num,\n            \"%.6e\" % r_a,\n            \"%.6e\" % r_r,\n            \"%.6f\" % ff_num,\n        ]\n\n        latex.append(   \" & \".join(text)+\"\\\\\"    )\n        err=r_r\n\n\n        \n        \n          \n    latex.append(\"\\end{tabular}\")\n\n    print \"\n\".join(latex)\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/9_Tests/2018.1.1/N05.py', '3', 'N05.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(262, 1689092601, 1686507380, 1686507380, 'Derivatives.py', 'Derivatives.py', 'def d_f_1(f,x,eps=1.0E-6):\n    #Simplest, somewhat naive\n    return (f(x+eps)-f(x))/eps\n\ndef d_f(f,x,eps=1.0E-6):\n    #Better\n    return (f(x+eps)-f(x-eps))/(2.0*eps)\n\ndef d2_f(f,x,eps=1.0E-6):\n    #Apply df (2*2=4 function evalutaions)\n    return (d_f(f,x+eps)-d_f(f,x-eps))/(2.0*eps)\n\ndef d2_f_1(f,x,eps=1.0E-6):\n    return (f(x+eps)+f(x-eps)-2.0*f(x))/(eps)**2\n\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/Code/Derivatives.py', '3', 'Derivatives.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(263, 1689092601, 1686507380, 1686507380, 'Gauss.py', 'Gauss.py', 'from Vector import *\nfrom Matrix import *\n\n\n\ndef Gauss_Forward_1(A,b=[]):\n    det=1.0\n    for i in range( len(A) ):\n        if (A[i][i]==0.0):\n            print \"Gauss_Forward_1: Matrix Singular at \",i,\"x\",i\n            return 0.0\n            \n        det*=A[i][i]\n        fact=1.0/A[i][i]\n        Matrices_Row_Mult(A,i,fact)\n        if (b): b[i]*=fact\n\n        for j in range( i+1,len(A) ):\n            if (b): b[j]-=A[j][i]*b[i]\n            Matrices_Rows_Operation(A,j,-A[j][i],i)\n\n    return det\n\n\n\n\n\ndef Gauss_Pivote(A,i):\n    pivote=i\n    piv=abs(A[i][i])\n    for j in range( i+1,len(A) ):\n        if ( abs(A[j][i])>piv ):\n            pivote=j\n            piv=abs(A[j][i])\n\n    return pivote\n\n\n\ndef Gauss_Forward(A,b=[]):\n    det=1.0\n    for i in range( len(A) ):\n        pivote=Gauss_Pivote(A,i)\n\n        if (pivote!=i):\n            if (b):\n                tmp=b[i]\n                b[i]=b[pivote]\n                b[pivote]=tmp\n            Matrices_Rows_Swap(A,i,pivote)\n            det*=-1.0\n        \n        if (A[i][i]==0.0):\n            print \"Gauss_Forward: Matrix Singular at \",i,\"x\",i\n            return 0.0\n            \n        det*=A[i][i]\n        fact=1.0/A[i][i]\n\n        #Always work on b first\n        if (b): b[i]*=fact\n        Matrices_Row_Mult(A,i,fact)\n\n        for j in range( i+1,len(A) ):\n            if (b): b[j]-=A[j][i]*b[i]\n            Matrices_Rows_Operation(A,j,-A[j][i],i)        \n            \n    return det\n\ndef Gauss_Backward(A,b=[]):\n    for i in range(len(A)-1,-1,-1):\n        for j in range(i):\n            if (b): b[j]-=A[j][i]*b[i]\n            Matrices_Rows_Operation(A,j,-A[j][i],i)\n        \ndef Gauss(A,b=[]):\n    det=Gauss_Forward(A,b)\n    print \"Gauss Forward, det\",det\n\n    if (det==0.0):\n        exit()\n    \n    Gauss_Backward(A,b)\n\n    return det\n    \n\ndef Gauss_Test(A,b=[]):\n    #Save for residual calc\n    AA=Matrix_Copy(A)\n    bb=list(b)\n    \n    det=Gauss(A,b)\n\n    #b is our solution, calc residual\n    res_b=Matrix_Mult_Vector(AA,b)\n    res_b=Vectors_Sub(res_b,bb)\n    \n    residual=Vector_Norm(res_b)/Vector_Norm(bb)\n    print \"Gauss_Test, Residual: %.6e\" %  residual\n\n    return det\n    \n\ndef Gauss2_Forward(A,B=[]):\n    det=1.0\n    for i in range( len(A) ):\n        pivote=Gauss_Pivote(A,i)\n\n        if (pivote!=i):\n            if (B): Matrices_Rows_Swap(B,i,pivote)\n            Matrices_Rows_Swap(A,i,pivote)\n            det*=-1.0\n        \n        if (A[i][i]==0.0):\n            print \"Gauss2_Forward: Matrix Singular at \",i,\"x\",i\n            return 0.0\n            \n        det*=A[i][i]\n        fact=1.0/A[i][i]\n        \n        #Always work on b first\n        if (B): Matrices_Row_Mult(B,i,fact)\n        Matrices_Row_Mult(A,i,fact)\n\n        for j in range( i+1,len(A) ):\n            if (B): Matrices_Rows_Operation(B,j,-A[j][i],i)\n            Matrices_Rows_Operation(A,j,-A[j][i],i)\n\n    return det\n\ndef Gauss2_Backward(A,B=[]):\n    for i in range(len(A)-1,-1,-1):\n        for j in range(i):\n            if (B): Matrices_Rows_Operation(B,j,-A[j][i],i)\n            Matrices_Rows_Operation(A,j,-A[j][i],i)\n            \ndef Gauss2(A,X=[]):\n    det=Gauss2_Forward(A,X)\n    print \"Gauss Forward, det\",det\n\n    if (det==0.0):\n        exit()\n    \n    Gauss2_Backward(A,X)\n\n    return det\n\ndef Gauss2_Test(A,B=[]):\n    #Save for residual calc\n    AA=Matrix_Copy(A)\n    BB=Matrix_Copy(B)\n    \n    det=Gauss2(A,B)\n    \n    #B is our solution, calc residual\n    R_B=Matrices_Mult(AA,B)\n    R_B=Matrices_Sub(R_B,BB)\n    \n    residual=Matrix_Norm(R_B)/Matrix_Norm(BB)\n    print \"Matrix_Gauss_Test, Residual: %.6e\" %  residual\n\n    return det\n\n\ndef Inverse(A):\n    X=Matrix_Identity( len(A) )\n\n    Gauss2_Test(A,X)\n\n    return X\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/Code/Gauss.py', '3', 'Gauss.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(264, 1689092601, 1686507380, 1686507380, 'Integration.py', 'Integration.py', 'def Trapezoid(f,x0,x1):\n    return 0.5*(x1-x0)*(f(x0)+f(x1))\n\ndef Trapezoids_1(f,x0,xn,n=100):\n    ## Repeat Trapezoid:\n    ## Divide in [x0,x1] em n intervals and calc\n\n    h=(xn-x0)/(1.0*n)\n    \n    trapez=0.0\n    x=x0\n    for i in range(n):\n        trapez+=Trapezoid(f,x,x+h)\n        x+=h\n\n    return trapez\n    \ndef Trapezoids_R(f,x0,xn,n=100):\n    ## Repeated Trapezoids:\n    ## Divide in [x0,x12] em n intervals and calc\n\n    h=(xn-x0)/(1.0*n)\n    \n    trapez=0.5*f(x0)\n    x=x0\n    for i in range(n-1):\n        trapez+=f(x+h)\n        x+=h\n        \n    trapez+=0.5*f(xn)\n\n    #print \"n=\",n,\"trap=\",h*trapez\n    \n    return h*trapez\n    \ndef Simpson(f,x0,x2):\n    x1=0.5*(x0+x2)\n    simp=f(x0)+4.0*f(x1)+f(x2)\n    \n    return simp*(x2-x1)/3.0\n\ndef Simpsons_1(f,x0,x2n,n=100):\n    ## Repeat Simpson:\n    ## Divide in [x0,x1] em n intervals and simply sum\n\n    h=(x2n-x0)/(1.0*n)\n    \n    simpson=0.0\n    x=x0\n    for i in range(n):\n        simpson+=Simpson(f,x,x+h)\n        x+=h\n\n    return simpson\n    \ndef Simpsons_R(f,x0,x2n,n=100):\n    ## Repeated Simpson\n\n    dx=(x2n-x0)/(1.0*n)\n    h=dx*0.5\n\n    simpson=f(x0)\n    x=x0+h\n    for i in range(n):\n        simpson+=4.0*f(x)\n        x+=dx\n    \n    x=x0+dx\n    for i in range(1,n):        \n        simpson+=2.0*f(x)\n        x+=dx\n\n    simpson+=f(x2n)\n\n    return h*simpson/3.0\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/Code/Integration.py', '3', 'Integration.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
(265, 1689092601, 1686507380, 1686507380, 'Matrix.py', 'Matrix.py', 'from Vector import *\n\ndef Matrix_Print(A):\n    print \"[\"\n    for i in range( len(A) ):\n        print \"   \",\n        Vector_Print(A[i])\n\n    print \"]\"\n\ndef Matrix_Copy(A):\n    B=[]\n    for i in range( len(A) ):\n        B.append([])\n        for j in range( len(A[i]) ):\n            B[i].append(A[i][j])\n\n    return B\n\ndef Matrix_Zero(m,n=0):\n    if (n==0): n=m\n    \n    O=[]\n    for i in range(m):\n        O.append([])\n        for j in range(n):\n            O[i].append(0.0)\n\n    return O\n\ndef Matrix_Identity(n):\n    I=Matrix_Zero(n)\n    for i in range(n):\n        I[i][i]=1.0\n\n    return I\n\ndef Matrix_Vandermonte(v):\n    V=[]\n    for i in range( len(v) ):\n        V.append([])\n        \n        fact=1.0\n        for j in range( len(v) ):\n            V[i].append(fact)\n            fact*=v[i]\n\n    return V\n\n\n\ndef Matrices_Add(A,B):\n    if (   len(A)!=len(B)   ):\n        print \"Matrices_Add: Matrices does not have same no of rows\"\n        exit()\n\n    C=[]\n    for i in range( len(A) ):\n        C.append(  []  )\n        if (   len(A[i])!=len(B[i])   ):\n            print \"Matrices_Add: Matrices does not have same no of columns\"\n            exit()\n\n        for j in range( len(A[i]) ):\n            C[i].append(   1.0*(A[i][j]+B[i][j])   )\n            \n    return C\n\ndef Matrices_Sub(A,B):\n    if (   len(A)!=len(B)   ):\n        print \"Matrices_Add: Matrices does not have same no of lines\"\n        exit()\n\n    C=[]\n    for i in range( len(A) ):\n        C.append(  []  )\n        if (   len(A[i])!=len(B[i])   ):\n            print \"Matrices_Add: Matrices does not have same no of columns\"\n            exit()\n\n        for j in range( len(A[i]) ):\n            C[i].append(   1.0*(A[i][j]-B[i][j])   )\n            \n    return C\n\ndef Matrix_Mult_Scalar(A,a):\n    C=[]\n    for i in range( len(A) ):\n        C.append(  []  )\n        for 