(266, 1689092601, 1686507380, 1686507380, 'Polynomial.py', 'Polynomial.py', 'def Polynomia_Print(P):\n    texts=[]\n    for i in range( len(P) ):\n        text=\"%.6f\" % P[i]\n        if (i==0):   text=text+\"\"\n        elif (i==1): text=text+\"x\"\n        else:        text=text+\"x**\"+str(i)\n\n        if (i<len(P)-1 and P[i]>0.0):\n            text=\"+\"+text\n\n        if (abs(P[i])>0):\n            texts.append(text)\n\n    texts=list(reversed(texts))\n    \n    print \" \".join(texts)\n        \ndef Polynomias_Add(P,Q):\n    degree=max( len(P),len(Q) )\n    R=[]\n    for i in range( degree ):\n        R.append(0.0)\n        if (i<len(P)):\n            R[i]+=P[i]\n            \n        if (i<len(Q)):\n            R[i]+=Q[i]\n            \n    return R\n\ndef Polynomia_Calc_Dumb(P,x):\n    value=0.0\n    for i in range( len(P) ):\n        value+= P[i]*x**i\n        \n    return value\n\ndef Polynomia_Calc(P,x):\n    value=0.0\n    for i in range(len(P)-1,-1,-1 ):\n        value=value*x+P[i]\n        \n    return value\n\ndef Polynomia_Mult(P,c):\n    R=[]\n    for i in range( len(P) ):\n        R.append(  P[i]*c  )\n            \n    return R\n\ndef Polynomias_Mult(P,Q):\n    degree=len(P)+len(Q)-1\n    \n    R=[]\n    for i in range( degree ):\n        R.append(0.0)\n        \n    for i in range( len(P) ):\n        for j in range ( len(Q) ):\n            R[i+j]+=P[i]*Q[j]\n    return R\n\n\ndef Test():\n    P=[-1.0,1.0,1.0]\n    Q=[ 1.0,1.0]\n    Polynomia_Print(P)\n    Polynomia_Print(Q)\n\n\n    R=Polynomias_Mult(P,P)\n    R=Polynomias_Mult(P,R)\n    Polynomia_Print(R)\n\n\n    xmax=5.0\n    dx=1.0\n\n    x=-xmax\n    while (x<=xmax):\n        print x,Polynomia_Calc_Dumb(R,x),Polynomia_Calc(R,x)\n        x+=dx\n', '1', 0, NULL, '1', '1', '10', '1', '1', NULL, NULL, '/usr/local/Slides/1_Disciplines/1_CN/Code/Polynomial.py', '3', 'Polynomial.py', '1', NULL, NULL, NULL, NULL, NULL, NULL),
