(319, 1690997361, 1690253927, 1690997361, 'Circle', 'The Circle', 'Circle, center in origin, radius $r$ and angular velocity  $&#92;omega$. Parametrization:\r\n\r\n&#92;[\r\n &#92;begin{pmatrix}x(t)&#92;&#92;y(t)&#92;end{pmatrix}\r\n =\r\n &#92;begin{pmatrix}r &#92;cos{&#92;omega t}&#92;&#92;r &#92;sin{&#92;omega t}&#92;end{pmatrix}\r\n :=r &#92;underline{e}(&#92;omega t)\r\n&#92;]\r\nWhere:\r\n&#92;[\r\n   &#92;underline{e}( t)\r\n  =\r\n   &#92;begin{pmatrix}&#92;cos{ t}&#92;&#92; &#92;sin{ t}&#92;end{pmatrix}\r\n&#92;]', '2', 284, 1, '1', '', '19', '2', '', '', '', '', '5', '3', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(320, 1691506078, 1690478762, 1691506078, 'First Derivative', 'Velocity', 'First derivative:\r\n\r\n&#92;[\r\n &#92;begin{pmatrix}x&#039;(t)&#92;&#92;y&#039;(t)&#92;end{pmatrix}\r\n =\r\n &#92;omega\r\n &#92;begin{pmatrix}-r &#92;sin{&#92;omega t}&#92;&#92;r &#92;cos{&#92;omega t}&#92;end{pmatrix}\r\n :=r&#92;omega &#92;underline{f}(&#92;omega t)\r\n&#92;]\r\nWhere:\r\n&#92;[\r\n &#92;underline{f}(t)\r\n =\r\n &#92;begin{pmatrix}-&#92;sin{ t}&#92;&#92; &#92;cos{t}&#92;end{pmatrix}\r\n&#92;]\r\nNote:\r\n&#92;[\r\n   &#92;underline{e}&#039;(t)=&#92;underline{&#92;widehat e}(t)=&#92;underline{f}(t);\r\n   &#92;qquad\r\n   &#92;underline{f}&#039;(t)=&#92;underline{&#92;widehat f}(t)=-&#92;underline{e}(t);\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '1', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(321, 1690997027, 1690479758, 1690997027, 'Second Derivative', 'Aceleration', 'Second derivative:\r\n\r\n&#92;[\r\n &#92;begin{pmatrix}x&#039;&#039;(t)&#92;&#92;y&#039;&#039;(t)&#92;end{pmatrix}\r\n =\r\n  &#92;omega^2\r\n &#92;begin{pmatrix}-r &#92;cos{&#92;omega t}&#92;&#92;-r &#92;sin{&#92;omega t}&#92;end{pmatrix}\r\n =\r\n -&#92;omega^2&#92;underline{r}(t)\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '2', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(322, 1690997062, 1690479826, 1690997062, 'Velocity', 'Velocity', 'Scalar velocity:\r\n\r\n&#92;[\r\n v(t)=r&#92;omega &#92;sqrt{ &#92;sin^2{&#92;omega t}+&#92;cos^2{&#92;omega t} }\r\n =\r\n &#92;omega  r\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '3', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(323, 1690997188, 1690480116, 1690997188, 'Determinant', 'Determinant', 'Determinant:\r\n\r\n&#92;[\r\n d(t)=\r\n &#92;begin{vmatrix}\r\n -r &#92;omega sin{&#92;omega t} &amp; -r &#92;omega^2 &#92;cos{&#92;omega t}\r\n &#92;&#92;\r\n r &#92;omega &#92;cos{&#92;omega t} &amp; -r &#92;omega^2 &#92;sin{&#92;omega t}\r\n &#92;end{vmatrix}\r\n =\r\n r^2&#92;omega^3(&#92;sin^2{&#92;omega t}+&#92;cos^2&#92;omega {t})\r\n =\r\n r^2&#92;omega^3\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '4', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(324, 1691506642, 1691505615, 1691506642, 'Angular Velocity', 'Angular Velocity', '&#92;[\r\n &#92;omega(t)=\r\n &#92;frac{d(t)}{v(t)^2}\r\n &#92;frac{r^2&#92;omega^3}{(r &#92;omega)^2}\r\n =&#92;omega\r\n&#92;]\r\n\r\n&#92;[\r\n &#92;nu(t)=&#92;frac{1}{&#92;omega(t)}\r\n =&#92;frac{1}{&#92;omega}\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '6', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(325, 1691506635, 1691505663, 1691506635, 'Curvature', 'Curvature', '&#92;[\r\n &#92;kappa(t)=&#92;frac{d(t)}{v(t)^3}\r\n =\r\n &#92;frac{r^2 &#92;omega^3}{(r&#92;omega)^3}\r\n =\r\n &#92;frac{1}{r}\r\n&#92;]\r\nRatio:\r\n&#92;[\r\n &#92;rho(t)=&#92;frac{1}{&#92;kappa(t)}=r\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '7', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(326, 1691506837, 1691506605, 1691506837, 'Frenet', 'Frenet', '&#92;[\r\n &#92;underline{t}(t)=&#92;frac{r &#92;underline{f}(t)}{r}\r\n  =\r\n  &#92;underline{f}(t);\r\n  &#92;qquad\r\n  &#92;underline{n}(t)=-&#92;frac{r &#92;underline{e}(t)}{r}\r\n  =\r\n  -&#92;underline{e}(t).\r\n&#92;]', '2', 319, 1, '1', '', '7', '2', '', '', '', '', '5', '5', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(327, 1691510790, 1691510790, 1691510790, 'Evolute', 'Evolute', 'Curve of Center of Curvatures\r\n&#92;[\r\n   &#92;underline{c}(t)\r\n  =\r\n   &#92;underline{r}(t)\r\n  +\r\n   &#92;rho(t) &#92;underline{n}(t)\r\n  =\r\n  r&#92;underline{e}(t)+r(-&#92;underline{e}(t))\r\n  =&#92;underline{0}\r\n&#92;]', '2', 319, 1, '1', '1', '7', '2', '1', '', '', '', '5', '8', '1', 'JS/Curves/Circle.js', '', NULL, NULL, NULL, NULL),
(328, 1691515315, 1691514235, 1691515315, 'Trochoids', 'Trochoids', 'Rolling a circle of radius $r$ alogn the $x$-axis.\r\nParametrization:\r\n\r\n&#92;[\r\n &#92;underline{r}(t)\r\n =\r\n &#92;begin{pmatrix}x(t)&#92;&#92;y(t)&#92;end{pmatrix}\r\n =\r\n &#92;begin{pmatrix}\r\n rt -b&#92;cos{ t}&#92;&#92;r-b&#92;sin{t}&#92;end{pmatrix}\r\n :=\r\n r\r\n &#92;left(\r\n t &#92;underline{i}+&#92;underline{j}\r\n +&#92;lambda &#92;underline{q}(t)\r\n &#92;right)\r\n&#92;]\r\nWhere $&#92;lambda=&#92;frac{b}{r}$, dimensionless.', '2', 284, 1, '1', '', '19', '2', '', '', '', '', '5', '4', '1', 'JS/Curves/Trochoids.js', '', NULL, NULL, NULL, NULL),
(329, 1691520230, 1691515003, 1691520230, 'Vectors', 'Vectors', 'Vectors\r\n&#92;[\r\n &#92;underline{p}( t)\r\n =\r\n &#92;begin{pmatrix}-&#92;cos{ t}&#92;&#92; &#92;sin{ t}&#92;end{pmatrix};\r\n &#92;qquad\r\n &#92;underline{q}( t)\r\n =\r\n &#92;begin{pmatrix}-&#92;sin{ t}&#92;&#92; -&#92;cos{ t}&#92;end{pmatrix}\r\n&#92;]\r\nNote:\r\n&#92;[\r\n &#92;underline{&#92;widehat p}(t)=&#92;underline{q}(t);\r\n &#92;qquad\r\n &#92;underline{&#92;widehat q}(t)=-&#92;underline{p}(t);\r\n&#92;]\r\nHowever:\r\n&#92;[\r\n &#92;underline{ p}&#039;(t)=-&#92;underline{q}(t);\r\n &#92;qquad\r\n &#92;underline{q}&#039;(t)=&#92;underline{p}(t);\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '1', '1', '', '', NULL, NULL, NULL, NULL),
(330, 1691515809, 1691515674, 1691515809, 'Derivative', 'Derivative', '&#92;[\r\n &#92;underline{r}&#039;( t)\r\n =\r\n r&#92;left(\r\n &#92;underline{i}+&#92;lambda &#92;underline{p}(t)\r\n &#92;right)\r\n&#92;]\r\n&#92;[\r\n &#92;underline{&#92;widehat r}&#039;( t)\r\n =\r\n r&#92;left(\r\n &#92;underline{j}+&#92;lambda &#92;underline{q}(t)\r\n &#92;right)\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '2', '1', '', '', NULL, NULL, NULL, NULL),
(331, 1691516490, 1691515891, 1691516490, '2nd Derivative', '2nd Derivative', '&#92;[\r\n &#92;underline{r}&#039;&#039;( t)\r\n =\r\n -r&#92;lambda &#92;underline{p}(t)\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '4', '1', '', '', NULL, NULL, NULL, NULL),
(332, 1691643405, 1691516042, 1691643405, 'Velocity', 'Velocity', '&#92;[\r\n v(t)^2\r\n =\r\n r^2(1+&#92;lambda^2-2&#92;lambda &#92;cos{t})\r\n&#92;]\r\nIn the cycloid case ($&#92;lambda=1$):\r\n&#92;[\r\n v(t)^2\r\n =\r\n 2r^2(1- &#92;cos{t})\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '3', '1', '', '', NULL, NULL, NULL, NULL),
(333, 1691516411, 1691516270, 1691516411, 'Determinant', 'Determinant', 'As:\r\n&#92;[\r\n   &#92;underline{j} &#92;cdot &#92;underline{q}(t)\r\n   =-&#92;cos{t}\r\n&#92;]\r\n&#92;[\r\n d(t)\r\n =\r\n &#92;underline{&#92;widehat r}&#039;(t) &#92;cdot &#92;underline{r}&#039;&#039;(t)\r\n =\r\n r^2&#92;lambda (&#92;cos{t}-&#92;lambda)\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '5', '1', '', '', NULL, NULL, NULL, NULL),
(334, 1691643461, 1691516668, 1691643461, 'Singular points', 'Singular points', '&#92;[\r\n &#92;underline{r}&#039;(t)\r\n =\r\n &#92;underline{0}\r\n &#92;qquad &#92;Leftrightarrow &#92;qquad\r\n &#92;underline{i}=-&#92;lambda &#92;underline{p}(t)\r\n&#92;]\r\nSatisfied, if and only in the Cycloid case, $&#92;lambda=1$, ie. $b=r$, and:\r\n&#92;[\r\n t=2 p &#92;pi,\r\n ~p &#92;in &#92;mathbb{Z}\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '6', '1', '', '', NULL, NULL, NULL, NULL),
(335, 1691643538, 1691517215, 1691643538, 'Angular Velocity', 'Angular Velocity', '&#92;[\r\n &#92;omega(t)\r\n =\r\n &#92;lambda\r\n &#92;frac{ &#92;cos{t}-&#92;lambda}{1+&#92;lambda^2-2 &#92;lambda &#92;cos{t}}\r\n&#92;]\r\nIn the Cycloid case:\r\n&#92;[ \r\n   &#92;omega(t)\r\n   =\r\n&#92;frac{ &#92;cos{t}-1}{2(1-&#92;cos{t})^2}\r\n&#92;]', '2', 328, 1, '1', '', '12', '2', '', '', '', '', '5', '7', '1', '', '', NULL, NULL, NULL, NULL),
(336, 1691641916, 1691641259, 1691641916, 'Curvature', 'Curvature', 'Curvature:\r\n&#92;[\r\n &#92;kappa(t)=&#92;frac{d(t)}{v(t)^3}\r\n&#92;]\r\nCurvature ratio of Osculating Circle:\r\n&#92;[\r\n &#92;rho(t)=&#92;frac{v(t)^3}{d(t)}\r\n&#92;]', '1', 270, 1, '1', '', '7', '2', '', '', '', '', '2', '10', '1', '', '', NULL, NULL, NULL, NULL),
(337, 1691642226, 1691642196, 1691642226, 'Evolute', 'Evolute', 'Curve traced by curvature centers:\r\n&#92;[\r\n &#92;underline{c}(t)\r\n =\r\n &#92;underline{r}(t)\r\n +\r\n &#92;rho(t) &#92;underline{n}(t)\r\n =\r\n &#92;underline{r}(t)\r\n +\r\n &#92;nu(t) &#92;underline{&#92;widehat r}&#039;(t)\r\n&#92;]', '1', 270, 1, '1', '', '7', '2', '', '', '', '', '2', '11', '1', '', '', NULL, NULL, NULL, NULL);
